class Solution {
    public long maximumValueSum(int[][] board) {
        int n = board.length;
        int m = board[0].length;
        long MIN = -1000000000;
        int[][][] nb = new int[n][3][2];
        for(int i=0;i<n;i++){
            for(int j=0;j<3;j++){
                nb[i][j][0]=j;
                nb[i][j][1]=(int)MIN;
            }
        }

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                int minK = 0;
                boolean replace = false;
                for(int k=0;k<3;k++){
                    if(nb[i][k][1]<nb[i][minK][1]){
                        minK = k;
                    }
                    if(nb[i][k][1]<board[i][j]){
                        replace = true;
                    }
                }
                if(replace){
                    nb[i][minK][0] = j;
                    nb[i][minK][1] = board[i][j];
                }
            }
        }

        // for(int i=0;i<n;i++){
        //     System.out.println(nb[i][0][1]+","+nb[i][1][1]+","+nb[i][2][1]);
        // }

        long ans = MIN*3;
        for(int i1=0;i1<n;i1++){
            for(int j1=0;j1<3;j1++){
                for(int i2=i1+1;i2<n;i2++){
                    for(int j2=0;j2<3;j2++){
                        for(int i3=i2+1;i3<n;i3++){
                            for(int j3=0;j3<3;j3++){
                                if(nb[i1][j1][0]!=nb[i2][j2][0]&&
                                nb[i1][j1][0]!=nb[i3][j3][0]&&
                                nb[i3][j3][0]!=nb[i2][j2][0]){
                                    //System.out.println(ans+" "+i1+" "+i2+" "+i3);
                                    long t1 = nb[i1][j1][1];
                                    long t2 = nb[i2][j2][1];
                                    long t3 = nb[i3][j3][1];
                                    if(ans<t1+t2+t3){
                                        ans = t1+t2+t3;
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }


}
