class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size(), m = board[0].size();

        vector<pair<int,pair<int,int>>> val;
        for(int i=0 ; i<n ; i++) {
            for(int j=0 ; j<m ; j++) {
                val.push_back({board[i][j], {i, j}});
            }
        }

        sort(val.begin(), val.end(), greater<pair<int,pair<int,int>>>());

        long long ans = LONG_LONG_MIN;

        for(int i=0 ; i<n ; i++) {
            for(int j=0 ; j<m ; j++) {
                long long v1 = board[i][j];
                long long v2 = -1, v3 = -1;
                int r2 = -1, c2 = -1;
                int index = -1;
                for(int k=0 ; k<val.size() ; k++) {
                    int r = val[k].second.first, c = val[k].second.second;
                    if(r == i || c == j || r == r2 || c == c2) continue;
                    if(v2 == -1) {
                        v2 = val[k].first;
                        index= k;
                        r2 = r;
                        c2 = c;
                    }
                    else {
                        v3 = val[k].first;
                        break;
                    }
                }
                ans = max(ans, v1 + v2 + v3);
                r2 = -1, c2 = -1, v2 = -1, v3 = -1;
                for(int k=index+1 ; k<val.size() ; k++) {
                    int r = val[k].second.first, c = val[k].second.second;
                    if(r == i || c == j || r == r2 || c == c2) continue;
                    if(v2 == -1) {
                        v2 = val[k].first;
                        r2 = r;
                        c2 = c;
                    }
                    else {
                        v3 = val[k].first;
                        break;
                    }
                }
                if(v2 != -1 && v3 != -1) {
                    ans = max(ans, v1 + v2 + v3);
                }
            }
        }

        return ans;
    }
};
