class Solution {
public:

    int n, m;

    long long dp[105][105][105][3];

    long long memo(int i, int j1, int j2,int cnt,  vector<vector<int>> &board, vector<vector<pair<int,int>>> &g){

        if(cnt){
            return 0;
        }
        if(i== n){

            return -1e11;
        }


        if(dp[i][j1+1][j2+1][cnt]!= -1e12){
            return dp[i][j1+1][j2+1][cnt];
        }

        long long ans = -1e11;

        ans = max(ans, memo(i+1, j1, j2,cnt, board, g));


        for(int j = 0;j<3;j++){
            if(j1!= g[i][j].second && j2!= g[i][j].second){
                if(j1== -1){
                    ans= max(ans, memo(i+1, g[i][j].second, j2,0,  board, g)+ board[i][g[i][j].second]);
                }
                else{
                    if(j2== -1){
                        ans = max(ans, memo(i+1, j1, g[i][j].second, 0, board, g)+ board[i][g[i][j].second]);
                    }
                    else{
                        ans = max(ans, memo(i+1, j1, j2, 1, board, g)+ board[i][g[i][j].second]);
                        ans = max(ans, memo(i+1, j1, j2, 0, board, g));
                    }
                }
            }
        }

        return dp[i][j1+1][j2+1][cnt]= ans;
    }



    long long maximumValueSum(vector<vector<int>>& board) {

        n = board.size();
        m = board[0].size();

       vector<vector<pair<int,int>>> g(n, vector<pair<int,int>> (m));

        for(int i=0;i<n;i++){
            for(int j = 0;j<m;j++){
                g[i][j] = {board[i][j], j};
            }
        }

        for(int i= 0;i< 105; i++){
            for(int j= 0;j<105;j++){
                for(int k = 0;k< 105;k++){
                    dp[i][j][k][0] = -1e12;
                    dp[i][j][k][1] = -1e12;
                }
            }
        }

        for(auto &it: g){
            sort(it.begin(), it.end());
            reverse(it.begin(), it.end());
        }

        long long ans = memo(0, -1, -1, 0, board, g);

        return ans;
    }
};
