class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long m = board.size();
        long long n = board[0].size();
        long long maxi = LLONG_MIN;
        vector<vector<pair<long long, long long>>> row_max(m);
        for (int i = 0; i < m; ++i) 
        {
           vector<pair<long long, long long>> cols;
           for (long long j = 0; j < n; ++j) 
           {
              cols.push_back({board[i][j], j});
           }
           sort(cols.rbegin(), cols.rend()); 
           row_max[i] = vector<pair<long long, long long>>(cols.begin(), cols.begin() + 3);
        }
        for (long long r1 = 0; r1 < m; ++r1) 
        {
           for (long long r2 = r1 + 1; r2 < m; ++r2) 
           {
              for (long long r3 = r2 + 1; r3 < m; ++r3) 
              {
                vector<tuple<long long, long long, long long>> result;
                for (const auto& p1 : row_max[r1]) 
                {
                    for (const auto& p2 : row_max[r2]) 
                    {
                        if (p1.second == p2.second) 
                        {
                            continue;
                        }
                        for (const auto& p3 : row_max[r3]) 
                        {
                            if (p3.second == p1.second || p3.second == p2.second) 
                            {
                                continue;
                            }
                            result.push_back(make_tuple(p1.first + p2.first + p3.first, p1.second, p2.second));
                        }
                    }
                }
                for (const auto& res : result) 
                {
                    long long sum = get<0>(res);
                    if(maxi<sum)
                    {
                        maxi=sum;
                    }
                }
              }
            }
          }
    return maxi;
    }
};
