class Solution {
public:

long long maximumValueSum(vector<vector<int>>& board) {
    int m=board.size();
    int n=board[0].size();
    vector<vector<pair<long long, int>>>help(m,vector<pair<long long, int>>(n));
    for(int i=0;i<m;++i){
        for(int j=0;j<n;++j){
            help[i][j].first=board[i][j];
            help[i][j].second=j;
        }
        sort(help[i].begin(),help[i].end(),greater<pair<int, int>>());
        help[i].resize(3);
    }
    long long res=LONG_LONG_MIN;
    map<int,vector<pair<int,int>>>h;
    for(int i=0;i<help.size();++i){
        for(int j=0;j<help[0].size();++j){
            h[help[i][j].second].push_back({help[i][j].first, i});
        }
    }

    for(auto & v:h){
        sort(v.second.begin(),v.second.end(),greater<pair<int,int>>());
        if(v.second.size()>3){
            v.second.resize(3);
        }
    }
    vector<vector<long long>>tmp;
    for(auto v:h){
        for(auto t:v.second){
            tmp.push_back({t.second,v.first,t.first});
        }
    }
    for(int i=0;i<tmp.size();++i){
        for(int j=0;j<tmp.size();++j){
            for(int k=0;k<tmp.size();++k){
                if(tmp[i][0]!=tmp[j][0] && tmp[i][1]!=tmp[j][1] && tmp[i][0]!=tmp[k][0] && tmp[i][1]!=tmp[k][1] && tmp[j][0]!=tmp[k][0] && tmp[j][1]!=tmp[k][1]){
                    res=max(res,tmp[i][2]+tmp[j][2]+tmp[k][2]);
                }
            }
        }
    }



    return res;
}

};
