class Solution {
public:

    unordered_map<int,priority_queue<pair<int,int> , vector<pair<int,int>>, greater<pair<int,int>> >> mp;

    long long maximumValueSum(vector<vector<int>>& board) {
        int m=board.size();
        int n=board[0].size();

        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                // mx[i]=max(mx[i],board[j][i]);
                mp[i].push({board[j][i],j});
                if(mp[i].size()>3) mp[i].pop();


            }

        }
        vector<vector<pair<int,int>>> mx(n,vector<pair<int,int>>(3));
        for(int i=0;i<n;i++){
            mx[i][2]=mp[i].top();
            mp[i].pop();
            mx[i][1]=mp[i].top();
            mp[i].pop();
            mx[i][0]=mp[i].top();
        }
        long long ans=LLONG_MIN;
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    int f=0;
                    for(int ind1=1;ind1<=3;ind1++){
                        for(int ind2=1;ind2<=3;ind2++){
                            for(int ind3=1;ind3<=3;ind3++){

                                int row1=mx[i][ind1-1].second;

                                int row2=mx[j][ind2-1].second;
                                int row3=mx[k][ind3-1].second;
                                long long ele1=mx[i][ind1-1].first;
                                long long ele2=mx[j][ind2-1].first;
                                long long ele3=mx[k][ind3-1].first;
                                if(row1!=row2&&row2!=row3&&row3!=row1) {ans=max(ans,ele1+ele2+ele3);}




                            }
                            //if(f==1) break;
                        }
                        // if(f==1) break;
                    }


                }
            }
        }


        return ans;

    }
};
