/**
 * @param {number[][]} board
 * @return {number}
 */
var maximumValueSum = function(board) {
    const numRows = board.length;
    const numCols = board[0].length;
    let highestSum = Number.MIN_SAFE_INTEGER;
    const optimalValues = Array.from({ length: numRows }, () => Array(3).fill(Number.MIN_SAFE_INTEGER));
    const optimalColumns = Array.from({ length: numRows }, () => Array(3).fill(-1));

    for (let row = 0; row < numRows; ++row) {
        const columnValues = [];
        for (let col = 0; col < numCols; ++col) {
            columnValues.push([board[row][col], col]);
        }
        columnValues.sort((a, b) => b[0] - a[0]);

        for (let idx = 0; idx < 3 && idx < numCols; ++idx) {
            optimalValues[row][idx] = columnValues[idx][0];
            optimalColumns[row][idx] = columnValues[idx][1];
        }
    }

    for (let firstRow = 0; firstRow < numRows; ++firstRow) {
        for (let secondRow = firstRow + 1; secondRow < numRows; ++secondRow) {
            for (let thirdRow = secondRow + 1; thirdRow < numRows; ++thirdRow) {
                for (let firstIdx = 0; firstIdx < 3; ++firstIdx) {
                    for (let secondIdx = 0; secondIdx < 3; ++secondIdx) {
                        if (optimalColumns[secondRow][secondIdx] === optimalColumns[firstRow][firstIdx]) continue;
                        for (let thirdIdx = 0; thirdIdx < 3; ++thirdIdx) {
                            if (optimalColumns[thirdRow][thirdIdx] === optimalColumns[firstRow][firstIdx] || optimalColumns[thirdRow][thirdIdx] === optimalColumns[secondRow][secondIdx]) continue;

                            const sum = optimalValues[firstRow][firstIdx] + optimalValues[secondRow][secondIdx] + optimalValues[thirdRow][thirdIdx];
                            highestSum = Math.max(highestSum, sum);
                        }
                    }
                }
            }
        }
    }

    return highestSum;
};
