class Solution {
public:	
	long long maximumValueSum(const vector<vector<int>>& board) {
		int m = board.size();
		int n = board[0].size();
		long long maxSum = LLONG_MIN;
		vector<vector<int>> map(m, vector<int>(3));
		if (n <= 3) {
			for (int i1 = 0; i1 < m; ++i1) {
				for (int j1 = 0; j1 < n; ++j1) {
					for (int i2 = 0; i2 < m; ++i2) {
						if (i2 != i1)
							for (int j2 = 0; j2 < n; ++j2) {
								if (j2 != j1)
									for (int i3 = 0; i3 < m; ++i3) {
										if (i3 != i1 && i3 != i2)
											for (int j3 = 0; j3 < n; ++j3) {
												if (j1 != j3 && j2 != j3) {
													long long currentSum = (long long)board[i1][j1] + board[i2][j2] + board[i3][j3];
													maxSum = max(maxSum, currentSum);
												}
											}
									}
							}
					}
				}
			}
			return maxSum;
		}

		int cnt = 0;
		for (auto& l : board) {
			int mx0 = INT_MIN, mx1 = INT_MIN, mx2 = INT_MIN;
			int i0 = -1, i1 = -1, i2 = -1;
			for (int i = 0; i < n; i++) {
				if (l[i] > mx0) {
					mx2 = mx1;
					i2 = i1;
					mx1 = mx0;
					i1 = i0;
					mx0 = l[i];
					i0 = i;
				}
				else if (l[i] > mx1) {
					mx2 = mx1;
					i2 = i1;
					mx1 = l[i];
					i1 = i;
				}
				else if (l[i] > mx2) {
					mx2 = l[i];
					i2 = i;
				}
			}
			map[cnt][0] = i0;
			map[cnt][1] = i1;
			map[cnt][2] = i2;
			cnt++;
		}

		for (int i1 = 0; i1 < m; ++i1) {
			for (int j1 : map[i1]) {
				for (int i2 = 0; i2 < m; ++i2) {
					if (i2 != i1)
						for (int j2 : map[i2]) {
							if (j2 != j1)
								for (int i3 = 0; i3 < m; ++i3) {
									if (i3 != i1 && i3 != i2)
										for (int j3 : map[i3]) {
											if (j1 != j3 && j2 != j3) {
												long long currentSum = (long long)board[i1][j1] + board[i2][j2] + board[i3][j3];
												maxSum = max(maxSum, currentSum);
											}
										}
								}
						}
				}
			}
		}
		return maxSum;
	}
};
