class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        // long long dp[m+1][n+1][3];
        long long ans = -1e18;
        vector<vector<pair<long long,long long>>>arr(m,vector<pair<long long,long long>>(3));
        for(int i=0;i<m;i++){
            priority_queue<pair<int,int>>pq;
            for(int j=0;j<n;j++){
                pair<int,int>p;
                p.first = board[i][j];
                p.second= j;
                pq.push(p);
            }
            arr[i][0] =pq.top();
            pq.pop();
            arr[i][1] = pq.top();
            pq.pop();
            arr[i][2] = pq.top();
        }
        for(int i=0;i<m;i++){
            for(int j=0;j<3;j++){
                long long mx1 = arr[i][j].first;
                int col1 = arr[i][j].second;
                long long mx2  = -1e18;
                for(int i1=0;i1<m;i1++){
                    for(int j1 = 0;j1<3;j1++){
                        if(i==i1 | arr[i1][j1].second==col1)continue;
                        long long a2 = arr[i1][j1].first;
                        int col2 = arr[i1][j1].second;
                        long long mx3 = -1e18;
                        for(int i2=0;i2<m;i2++){
                            for(int j2 = 0;j2<3;j2++){
                                long long a4 = arr[i2][j2].first;
                                if(i2==i1 | i==i2 | arr[i2][j2].second==col2 | col1==arr[i2][j2].second)continue;
                                else mx3 = max(mx3,a4);
                            }
                        }
                        mx2 = max(mx2,a2+mx3);
                    }
                }
                ans = max(ans,mx1+mx2);
            }
        }
        // sort(arr2.begin(),arr2.end());
        // ans = arr2[n-1]+arr2[n-2]+arr2[n-2];
        return ans;
    }
};
