class Solution {
public:

    long long helper(int r1, int r2, int r3,vector<int>&a, vector<int>&b, vector<int>&c, vector<vector<int>>&board){

        int n = board[0].size();

        long long ans = LONG_LONG_MIN;
        vector<bool>vis(n,false);
        for(int i =0;i<3;i++){
            vis[a[i]] = true;
            for(int j=0;j<3;j++){
                if(vis[b[j]]) continue;
                vis[b[j]] = true;
                for(int k=0;k<3;k++){
                    if(vis[c[k]]) continue;
                    vis[c[k]] = true;
                    ans = max(ans,(long long)board[r1][a[i]]+(long long)board[r2][b[j]]+ (long long)board[r3][c[k]]);
                    vis[c[k]] = false;
                }
                vis[b[j]] = false;
            }
            vis[a[i]] = false;
        }

        return ans;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        long long ans = LONG_LONG_MIN;
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>pq;
        vector<vector<int>>v(m);

        for(int i =0;i<m;i++){
            for(int j =0;j<n;j++){
                pq.push({board[i][j],j});
                if(j>2) pq.pop();
            }

            while(!pq.empty()){
                v[i].push_back(pq.top().second);pq.pop();
            }
        }

        for(int i =0;i<m;i++){
            for(int j = i+1;j<m;j++){
                for(int k =j+1;k<m;k++ ){
                    // placed in i,j,k rows.
                    ans = max(ans,helper(i,j,k,v[i],v[j],v[k],board));

                }
            }
        }
        return ans;
    }
};
