class Solution {
    long long m,n;
    vector<pair<long long, long long>> fn(vector<int>& arr) {
        vector<pair<long long, long long>> res(3, {LLONG_MIN, -1});
        for (long long i=0; i<n; ++i) {
            if (arr[i] > res[0].first) {
                res[2] = res[1];
                res[1] = res[0];
                res[0] = {arr[i], i};
            } 
            else if (arr[i] > res[1].first) {
                res[2] = res[1];
                res[1] = {arr[i], i};
            } 
            else if (arr[i] > res[2].first)
                res[2] = {arr[i], i};
        }
        return res;
    }
    long long maxxer(vector<pair<long long, long long>>& v1, vector<pair<long long, long long>>& v2, vector<pair<long long, long long>>& v3) {
        long long ans = LLONG_MIN;
        for(int i=0; i<3; i++) {
            for(int j=0; j<3; j++) {
                for(int k=0; k<3; k++) {
                    if(v1[i].second!=v2[j].second and v1[i].second!=v3[k].second and v3[k].second!=v2[j].second)
                        ans = max(ans, v1[i].first+v2[j].first+v3[k].first);
                }
            }
        }
        return ans;
    }
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        m = board.size(), n = board[0].size();
        vector<vector<pair<long long,long long>>> v(m, vector<pair<long long,long long>>(3,{LLONG_MIN,-1}));
        for(long long i=0; i<m; i++)
            v[i] = fn(board[i]);
        long long ans = LLONG_MIN;
        for(long long i=0; i<m-2; i++) {
            for(long long j=i+1; j<m-1; j++) {
                for(int k=j+1; k<m; k++) {
                    ans = max(ans, maxxer(v[i], v[j], v[k]));
                }
            }
        }
        return ans;
    }
};
