class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& matrix) {
        int m = matrix.size();
        int n = matrix[0].size();

        // To store the top 3 elements from each row
        vector<vector<pair<int, int>>> topRows(m, vector<pair<int, int>>());
        // To store the top 3 elements from each column
        vector<vector<pair<int, int>>> topCols(n, vector<pair<int, int>>());

        // Populate topRows
        for (int i = 0; i < m; i++) {
            vector<pair<int, int>> rowElements;
            for (int j = 0; j < n; j++) {
                rowElements.push_back({matrix[i][j], j});
            }
            sort(rowElements.rbegin(), rowElements.rend());
            topRows[i] = vector<pair<int, int>>(rowElements.begin(), rowElements.begin() + min(3, (int)rowElements.size()));
        }

        // Populate topCols
        for (int j = 0; j < n; j++) {
            vector<pair<int, int>> colElements;
            for (int i = 0; i < m; i++) {
                colElements.push_back({matrix[i][j], i});
            }
            sort(colElements.rbegin(), colElements.rend());
            topCols[j] = vector<pair<int, int>>(colElements.begin(), colElements.begin() + min(3, (int)colElements.size()));
        }

        long long maxSum = LLONG_MIN;

        // Try all combinations from topRows and topCols to find the maximum sum
        for (int i = 0; i < m; i++) {
            for (auto [val1, col1] : topRows[i]) {
                for (int j = i + 1; j < m; j++) {
                    for (auto [val2, col2] : topRows[j]) {
                        if (col1 != col2) {
                            for (int k = j + 1; k < m; k++) {
                                for (auto [val3, col3] : topRows[k]) {
                                    if (col1 != col3 && col2 != col3) {
                                        maxSum = max(maxSum, (long long)val1 + val2 + val3);
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
