class Solution {
    int[][] board;
    int m;
    int n;
    long res =Long.MIN_VALUE;
    public long maximumValueSum(int[][] board) {
        this.board = board;
        m=board.length;
        n=board[0].length;
        HashSet<Integer>[] sets= new HashSet[m];
        Arrays.setAll(sets,i->new HashSet());
        for(int i=0;i<m;i++){
            int a1=Integer.MIN_VALUE;
            int a2=Integer.MIN_VALUE;
            int a3=Integer.MIN_VALUE;
            for(int j=0;j<n;j++){
                int x=board[i][j];
                if(x>a1){
                    a3=a2;
                    a2=a1;
                    a1=x;
                }else if(x>a2){
                    a3=a2;
                    a2=x;
                }else if(x>a3){
                    a3=x;
                }   
            }
            for(int j=0;j<n;j++){
                int x=board[i][j];
                if(x==a1||x==a2||x==a3){
                    sets[i].add(j);
                    sets[i].add(j);
                    sets[i].add(j);
                }
            }

            // System.out.println(a1 + " "+a2+ " "+a3);
        }
        for(int i=0;i<m-2;i++){
            for(int j:sets[i]){
                for(int k=i+1;k<m-1;k++){
                    for(int l:sets[k]){
                        if(l==j ){
                            continue;
                        }
                        for(int p=k+1;p<m;p++){
                            for(int q:sets[p]){
                                if(q==j || q==l ){
                                    continue;
                                }
                                long tmp = (long)board[i][j] + (long)board[k][l] +(long)board[p][q];
                                res=Math.max(res,tmp);
                            }
                        }
                    }
                }
            }
        }
        return res;
    }

}
