class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {

        vector<vector<pair<int,int>>>v;
        int N = board.size();
        int M = board[0].size();

        for(int i = 0; i < N; i++){
            vector<pair<int,int>>temp;
            for(int j = 0;j  < M;j++) temp.push_back({board[i][j], j});
            sort(temp.rbegin(),temp.rend());
            while(temp.size() > 3) temp.pop_back();
            v.push_back(temp);
        }

        long long int ans = -1e12;

        for(int i = 0; i < N; i++){
            for(int j = 0; j < N; j++){
                if(i == j) continue;
                for(int k = 0; k < N; k++){
                    if(i == j || j == k || k == i) continue;

                    for(int l = 0; l < v[i].size(); l++){
                        for(int m = 0; m < v[j].size(); m++){
                            for(int n = 0; n < v[k].size(); n++){
                                if(v[i][l].second == v[j][m].second || 
                                v[j][m].second == v[k][n].second ||
                                v[i][l].second == v[k][n].second) continue;
                                // cout<<"in\n";
                                ans = max(ans, 0ll + v[i][l].first + v[j][m].first + v[k][n].first);
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
