typedef long long ll;
class Solution {
public:
unordered_map<ll,ll>col;
map<pair<ll,vector<ll>>,ll>mp;
vector<vector<pair<ll,ll>>>m;
long long solve(int i,vector<vector<int>>&v,vector<ll>&v1){
if(v1.size()==3)return 0;
if(i>=v.size())return (1e18)*(-1);
if(mp[{i,v1}]!=0)return mp[{i,v1}];
long long ans=(1e18)*(-1);
for(int j=i;j<v.size();j++){
        for(int k=0;k<3;k++){
           if(col[m[j][k].second]==0) {
            col[m[j][k].second]++; v1.push_back(m[j][k].second);
            ans=max(ans,m[j][k].first+solve(j+1,v,v1));
            col[m[j][k].second]--;v1.pop_back();}
        }
    }
return mp[{i,v1}]=ans;
}

    long long maximumValueSum(vector<vector<int>>& v) {
        int n=v.size();
        m.resize(n);
        for(int i=0;i<n;i++){
            vector<pair<ll,ll>>maxi;
    for(int k=0;k<v[0].size();k++){
        maxi.push_back({v[i][k],k});
        }
        sort(maxi.begin(),maxi.end());
        reverse(maxi.begin(),maxi.end());
        for(int j=0;j<3;j++){
            m[i].push_back(maxi[j]);
        }

        }
        //cout<<m.size();
        vector<ll>v1;
        return solve(0,v,v1);
    }
};
