#include <vector>
#include <algorithm>
#include <iostream>

using namespace std;

class Solution {
public:
    long long solve(int rows, vector<vector<long long>>& topValues, vector<vector<int>>& topColumns) {
        long long maxSum = LLONG_MIN;
        for (int r1 = 0; r1 < rows; ++r1) {
            for (int r2 = r1 + 1; r2 < rows; ++r2) {
                for (int r3 = r2 + 1; r3 < rows; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (topColumns[r2][j] == topColumns[r1][i]) continue; 
                            for (int k = 0; k < 3; ++k) {
                                if (topColumns[r3][k] == topColumns[r1][i] || topColumns[r3][k] == topColumns[r2][j]) continue;

                                long long currentSum = topValues[r1][i] + topValues[r2][j] + topValues[r3][k];
                                maxSum = max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }

    long long maximumValueSum(vector<vector<int>>& grid) {
        int rows = grid.size();
        int cols = grid[0].size();
        vector<vector<long long>> topValues(rows, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> topColumns(rows, vector<int>(3, -1));
        for (int r = 0; r < rows; ++r) {
            vector<pair<long long, int>> columnValues;
            for (int c = 0; c < cols; ++c) {
                columnValues.emplace_back(grid[r][c], c);
            }
            sort(columnValues.rbegin(), columnValues.rend());

            for (int k = 0; k < 3 && k < cols; ++k) {
                topValues[r][k] = columnValues[k].first;
                topColumns[r][k] = columnValues[k].second;
            }
        }
        return solve(rows, topValues, topColumns);
    }
};
