class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m,n = len(board),len(board[0])
        def gongji(cr1,cr2):
            x1,y1=cr1
            x2,y2=cr2
            if x1==x2 or y1==y2:
                return True
            return False

        MAX_VAL = [[x,y,board[x][y]] for x in range(m) for y in range(n)]
        # print(MAX_VAL)
        MAX_VAL.sort(key=lambda p:-p[2])
        # print(MAX_VAL)

        res = -inf
        limit = min(2*(m+n-1)+1,m*n)
        for t in range(m+n-1):
            x1,y1,ans = MAX_VAL[t]
            for i in range(t+1,limit-1):
                x2,y2,v2 = MAX_VAL[i]
                if not gongji([x1,y1],[x2,y2]):
                    ans += v2
                    for j in range(i+1,limit):
                        x3,y3,v3 = MAX_VAL[j]
                        if not gongji([x3,y3],[x2,y2]) and not gongji([x1,y1],[x3,y3]):
                            ans += v3
                            res = max(res,ans)
                            ans -= v3
                            break
                    ans -= v2

        return res
