class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n = board.size();
        int m = board[0].size();
        map<int,vector<pair<int,int>>> mp;
        for(int i = 0; i < n; i ++ ){
            for(int j = 0; j < m; j ++ ){
                mp[-board[i][j]].push_back({i,j});
            }
        }
        long long mx = -3000000000;

        vector<bool> row(n+5),col(m+5);

        auto dfs = [&](auto && self,int u,long long res)->void{
            if(u == 4){
                mx = max(mx,res);
                return;
            }
            int cnt = 0;
            for(auto [k,v]:mp){
                for(auto [x,y]:v){
                    if(!row[x] && !col[y]){
                        cnt ++;
                        row[x] = true;
                        col[y] = true;
                        self(self,u+1,res-k);
                        row[x] = false;
                        col[y] = false;
                        // for(int i = 0; i < n; i ++ ){
                        //     if(i == x)continue;  
                        //     if(row[i] || col[y])continue;
                        //     row[i] = true;
                        //     col[y] = true;
                        //     self(self,u+1,res+board[i][y]);
                        //     row[i] = false;
                        //     col[y] = false;
                        // }
                        // for(int j = 0; j < m; j ++ ){
                        //     if(j == y)continue;
                        //     if(row[x] || col[j])continue;
                        //     row[x] = true;
                        //     col[j] = true;
                        //     self(self,u+1,res+board[x][j]);
                        //     row[x] = false;
                        //     col[j] = false;
                        // }
                    }
                }
                if(cnt > 3)break;
            }  
        };
        dfs(dfs,1,0);
        return mx;
    }
};
