class Solution {
public:
    int m,n;
long long f(vector<vector<int>>&board,multiset<pair<int,pair<int,int>>>ms,int k,int skip){
    long long maxAns=-1e15;
    if(k==3) return 0;
    if(ms.size()==0) return -1e14;
            auto it=*ms.rbegin();
             ms.erase(prev(ms.end()));
            if(skip<4)maxAns=f(board,ms,k,skip+1);
             long long x=it.first;
             long long currI=it.second.first;
             long long currJ=it.second.second;
             for(int i=0;i<m;i++){
                auto it1=ms.find({board[i][currJ],{i,currJ}});
                if(it1!=ms.end())ms.erase(it1);
             }
             for(int i=0;i<n;i++){
                auto it1=ms.find({board[currI][i],{currI,i}});
                if(it1!=ms.end())ms.erase(it1);
             }
             return max(maxAns,x+f(board,ms,k+1,skip));
}
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans=0;
        m=board.size(),n=board[0].size();
        multiset<pair<int,pair<int,int>>>ms;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                ms.insert({board[i][j],{i,j}});
            }
        }
        multiset<pair<int,pair<int,int>>>temp=ms;
        return f(board,ms,0,0);
        for(int k=0;k<3;k++){
             auto it=*ms.rbegin();
             ms.erase(prev(ms.end()));
             int x=it.first;
             int currI=it.second.first;
             int currJ=it.second.second;
             for(int i=0;i<m;i++){
                auto it1=ms.find({board[i][currJ],{i,currJ}});
                if(it1!=ms.end())ms.erase(it1);
             }
             for(int i=0;i<n;i++){
                auto it1=ms.find({board[currI][i],{currI,i}});
                if(it1!=ms.end())ms.erase(it1);
             }
             ans+=x;
        }
        ms=temp;

        long long maxAns=ans;
        ans=0;
        ms.erase(prev(ms.end()));
        for(int k=0;k<3;k++){
            if(ms.size()==0) break;
             auto it=*ms.rbegin();
             ms.erase(prev(ms.end()));
             int x=it.first;
             int currI=it.second.first;
             int currJ=it.second.second;
             for(int i=0;i<m;i++){
                auto it1=ms.find({board[i][currJ],{i,currJ}});
                if(it1!=ms.end())ms.erase(it1);
             }
             for(int i=0;i<n;i++){
                auto it1=ms.find({board[currI][i],{currI,i}});
                if(it1!=ms.end())ms.erase(it1);
             }
             ans+=x;
        }

        return max(maxAns,ans);
    }
};
