import java.util.*;

class Solution {
    public long maximumValueSum(int[][] board) {
        int m = board.length;
        int n = board[0].length;
        long a = Long.MIN_VALUE;
        long[][] b = new long[m][3];
        int[][] c = new int[m][3];

        int i = 0;
        while (i < m) {
            List<Map.Entry<Long, Integer>> d = new ArrayList<>();
            int j = 0;
            while (j < n) {
                d.add(new AbstractMap.SimpleEntry<>((long) board[i][j], j));
                j = j + 1;
            }
            d.sort((x, y) -> Long.compare(y.getKey(), x.getKey())); 

            int k = 0;
            while (k < 3 && k < n) {
                b[i][k] = d.get(k).getKey();
                c[i][k] = d.get(k).getValue();
                k = k + 1;
            }
            i = i + 1;
        }

        int r = 0;
        while (r < m) {
            int s = r + 1;
            while (s < m) {
                int t = s + 1;
                while (t < m) {
                    int i2 = 0;
                    while (i2 < 3) {
                        int j2 = 0;
                        while (j2 < 3) {
                            if (c[s][j2] == c[r][i2]) {
                                j2 = j2 + 1;
                                continue;
                            }
                            int k2 = 0;
                            while (k2 < 3) {
                                if (c[t][k2] == c[r][i2] || c[t][k2] == c[s][j2]) {
                                    k2 = k2 + 1;
                                    continue;
                                }

                                long e = b[r][i2] + b[s][j2] + b[t][k2];
                                a = Math.max(a, e);
                                k2 = k2 + 1;
                            }
                            j2 = j2 + 1;
                        }
                        i2 = i2 + 1;
                    }
                    t = t + 1;
                }
                s = s + 1;
            }
            r = r + 1;
        }

        return a;
    }
}
