vector<int> solve(vector<int>& a){
    vector<pair<int,int>> v;
    for(int i=0;i<a.size();i++) v.push_back({a[i],i});
    sort(v.begin(),v.end());
    int ind=a.size();
    ind--;
    vector<int> ans;
    ans.push_back(v[ind].second);
    ans.push_back(v[ind-1].second);
    ans.push_back(v[ind-2].second);
    return ans;
}

long long say(vector<int> a, vector<int> b, vector<int> c,
    int r1,int r2,int r3,vector<vector<int>>& board){
    long long ans=(-3e9-1);
    for(int i=0;i<3;i++)
    {
        for(int j=0;j<3;j++)
        {
            for(int k=0;k<3;k++)
            {
                if(a[i]!=b[j] && b[j]!=c[k] && a[i]!=c[k])
                {
                    long long l1=board[r1][a[i]];
                    long long l2=board[r2][b[j]];
                    long long l3=board[r3][c[k]];
                    ans=max(ans,l1+l2+l3);
                }
            }
        }
    }
    return ans;
}

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int n=a.size();
        int m=a[0].size();

        vector<vector<int>> tt(n);
        for(int i=0;i<n;i++) tt[i]=solve(a[i]);
        long long ans=(-3e9-1);
        for(int i=0;i<n;i++)
        {
            for(int j=i+1;j<n;j++)
            {
                for(int k=j+1;k<n;k++)
                {
                    ans=max(ans,say(tt[i],tt[j],tt[k],i,j,k,a));
                }
            }
        }

        return ans;



    }
};
