class Solution {
    public long maximumValueSum(int[][] board) {
        long[][] x=new long[board.length*board[0].length][3];
        for(int i=0;i<board.length;i++)
        for(int j=0;j<board[0].length;j++){
            x[i*board[0].length+j][0]=board[i][j];
            x[i*board[0].length+j][1]=i;
            x[i*board[0].length+j][2]=j;
        }
        Arrays.sort(x,(a,b)->Long.compare(b[0], a[0]));
        long res=Long.MIN_VALUE;
        for(int k=0;k<4;k++){
        long ans=x[k][0];
        long a=x[k][1];
         long b=x[k][2];
         for(int i=0;i<Math.min(board.length*board[0].length,13);i++)
         {
            if(x[i][1]!=a&&x[i][2]!=b){
                long c=x[i][1];
                 long d=x[i][2];

            for(int j=0;j<Math.min(board.length*board[0].length,22);j++)
            {
                if(x[j][1]!=a&&x[j][2]!=b&&x[j][1]!=c&&x[j][2]!=d){
                res=Math.max(ans+x[i][0]+x[j][0],res);}
            }
            }
         }}
         return res;
    }
}
