class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])
        l = []
        for i in range(m):
            for j in range(n):
                l.append((board[i][j], i, j))
        l.sort(reverse=True)
        l = l[:180]
        n = len(l)
        ans = int(-3e9)
        for i in range(n):
            for j in range(i+1,n):
                for k in range(j+1,n):
                    if  l[i][1] != l[j][1] and l[j][1] != l[k][1] and l[i][1] != l[k][1] and l[i][2] != l[j][2] and l[j][2] != l[k][2] and l[i][2] != l[k][2]:
                        ans = max(ans, l[i][0]+l[j][0]+l[k][0])
        return ans
