class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long res = LONG_MIN;
        vector<vector<long long>> nums;
        for(int i = 0; i < board.size(); i++)
        {
            for(int j = 0; j < board[0].size(); j++)
            {
                nums.push_back({board[i][j],i,j});
            }
        }
        sort(nums.begin(),nums.end(), greater<>());
        int flag = -1;
        for(int i = 0; i < nums.size(); i++)
        {
            if(flag != -1 && i > flag + 1)
                break;
            for(int j = i+1; j < nums.size(); j++)
            {
                if(nums[j][1] == nums[i][1] || nums[j][2] == nums[i][2])
                    continue;
                for(int k = j+1; k < nums.size(); k++)
                {
                    if(nums[k][1] == nums[i][1] || nums[k][1] == nums[j][1] || nums[k][2] == nums[i][2] || nums[k][2] == nums[j][2])
                        continue;
                    else
                    {
                        long long temp = nums[i][0] + nums[j][0] + nums[k][0];
                        if(res >= temp)
                            break;
                        res = temp;
                        flag = k;
                    }
                }
            }
        }
        return res;
    }
};
