class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=sz(board),m=sz(board[0]);
        set<pii>high;
        rep(0,n){
            map<int,vi>tree;
            for(int j=0;j<m;j++)tree[board[i][j]].pb(j);
            int counter=0;
            for(auto it=tree.rbegin();;it++){
                for(int k=0;k<min(3,sz(it->second));k++){
                    high.emplace(i,it->second[k]);
                    counter++;
                }
                if(counter>=3)break;
            }
        }
        vector<pair<int,pii>>points;
        for(int j=0;j<m;j++){
            map<int,vi>tree;
            rep(0,n)tree[board[i][j]].pb(i);
            int counter=0;
            for(auto it=tree.rbegin();;it++){
                for(int k=0;k<min(3,sz(it->second));k++){
                    pii coord=pair(it->second[k],j);
                    if(high.contains(coord)){
                        points.pb(pair(board[coord.first][coord.second],coord));
                    }
                    counter++;
                }
                if(counter>=3)break;
            }
        }
        ll ans=-3e9;
        int l=sz(points);
        vi usedrow(3),usedcol(3);
        rep(0,l){
            usedrow[0]=points[i].second.first;
            usedcol[0]=points[i].second.second;
            for(int j=i+1;j<l;j++){
                int a=points[j].second.first;
                int b=points[j].second.second;
                if(a==usedrow[0]||b==usedcol[0])continue;
                usedrow[1]=a;
                usedcol[1]=b;
                for(int k=j+1;k<l;k++){
                    int c=points[k].second.first;
                    int d=points[k].second.second;
                    if(c!=usedrow[0]&&c!=usedrow[1]&&d!=usedcol[0]&&d!=usedcol[1]){
                        ans=max(ans,(ll)points[i].first+points[j].first+points[k].first);
                    }
                }
            }
        }
        return ans;
    }
};

