import java.util.*;

class Solution {
    public long maximumValueSum(int[][] board) {
        int n = board.length, m = board[0].length;
        List<long[]> now = new ArrayList<>();
        List<long[]>[] v = new ArrayList[n];

        for (int i = 0; i < n; i++) {
            v[i] = new ArrayList<>();
            for (int j = 0; j < m; j++) {
                now.add(new long[]{board[i][j], i, j});
                v[i].add(new long[]{board[i][j], j});
            }
            v[i].sort(Comparator.comparingLong(a -> a[0]));
        }

        now.sort((a, b) -> Long.compare(b[0], a[0]));

        long ans = Long.MIN_VALUE;

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i == j) continue;

                long cur = v[i].get(m - 1)[0];
                int j1 = (int) v[i].get(m - 1)[1], j2 = m - 1;

                if (v[j].get(m - 1)[1] == j1) j2--;

                cur += v[j].get(j2)[0];
                j2 = (int) v[j].get(j2)[1];

                for (long[] p : now) {
                    int x = (int) p[1], y = (int) p[2];
                    if (x != i && x != j && y != j1 && y != j2) {
                        ans = Math.max(ans, cur + p[0]);
                        break;
                    }
                }
            }
        }
        return ans;
    }
}
