class Solution {
public:
    vector<vector<int>> pq;
    long long one(int r1,int c1 ,int r2,int c2 ,vector<vector<int>> &mat){
        int n = mat.size();
        int m = mat[0].size();
        long long ans = LONG_MIN;
        for(int i=0;i<pq.size();i++){
            int r = pq[i][1];
            int c = pq[i][2];
            if(r==r1 or r==r2 or c==c1 or c==c2)continue;
            else return pq[i][0];
        }
        return ans;
    }
    long long two(int r,int c,vector<vector<int>> &mat){
        int n = mat.size();
        int m = mat[0].size();
        int row,col;
        long long ans = LONG_MIN;
        for(int i=0;i<n;i++){
            if(i==r)continue;
            for(int j=0;j<m;j++){
               if(j==c)continue;
               if(mat[i][j]>ans){
                row = i;
                col = j;
                ans = mat[i][j];
               }
            //ans = max(ans,mat[i][j]+one(r,c,i,j,mat));
            }
        }
        long long temp = LONG_MIN;
    //    for(int i=0;i<n;i++){
    //         if(i==r)continue;
    //         for(int j=0;j<m;j++){
    //             if(j==c)continue;
    //             if(mat[i][j]==ans){
    //                 temp = max(temp,ans+one(r,c,i,j,mat));
    //             }
    //         }
    //     }
    for(int i=0;i<n;i++){
        if(i==r)continue;
        temp = max(temp,mat[i][col]+one(r,c,i,col,mat));
    }
    for(int j=0;j<m;j++){
        if(j==c)continue;
        temp = max(temp,mat[row][j]+one(r,c,row,j,mat));
    }
    return temp;
    }
    long long maximumValueSum(vector<vector<int>>& mat) {
        int n = mat.size();
        int m = mat[0].size();
        long long ans = LONG_MIN;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                pq.push_back({mat[i][j],i,j});
            }
        }
        sort(pq.begin(),pq.end(),greater<vector<int>>());
        //reverse(pq.begin(),pq.end());
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                ans = max(ans,mat[i][j]+two(i,j,mat));
            }
        }
        return ans;
    }
};
