class Solution
{
public:
    long long maximumValueSum(vector<vector<int>> &board)
    {
        int m = board.size();
        int n = board[0].size();
        long long maxSum = LLONG_MIN;
        vector<vector<long long>> bv(m, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> bc(m, vector<int>(3, -1));

        for (int i = 0; i < m; ++i)
        {
            vector<pair<long long, int>> col_val;
            for (int j = 0; j < n; ++j)
            {
                col_val.emplace_back(board[i][j], j);
            }
            sort(col_val.rbegin(), col_val.rend());

            for (int k = 0; k < 3 && k < n; ++k)
            {
                bv[i][k] = col_val[k].first;
                bc[i][k] = col_val[k].second;
            }
        }

        for (int r1 = 0; r1 < m; ++r1)
        {
            for (int r2 = r1 + 1; r2 < m; ++r2)
            {
                for (int r3 = r2 + 1; r3 < m; ++r3)
                {
                    for (int i = 0; i < 3; ++i)
                    {
                        for (int j = 0; j < 3; ++j)
                        {
                            if (bc[r2][j] == bc[r1][i])
                                continue;
                            for (int k = 0; k < 3; ++k)
                            {
                                if (bc[r3][k] == bc[r1][i] || bc[r3][k] == bc[r2][j])
                                    continue;

                                long long cs = bv[r1][i] + bv[r2][j] + bv[r3][k];
                                maxSum = max(maxSum, cs);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
