#include <vector>
#include <queue>
#include <deque>
#include <algorithm>
#include <climits>
using namespace std;

class Solution {
public:

    long long maximumValueSum(vector<vector<int>>& b) {
        int m = b.size();
        int n = b[0].size();
        long long maxi = LLONG_MIN;


        vector<deque<pair<long long, int>>> tv(m);

        for (int i = 0; i < m; ++i) {
            help(b[i], tv[i]);
        }


        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (tv[r2][j].second == tv[r1][i].second) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (tv[r3][k].second == tv[r1][i].second || tv[r3][k].second == tv[r2][j].second) continue;
                                maxi = max(maxi, tv[r1][i].first + tv[r2][j].first + tv[r3][k].first);
                            }
                        }
                    }
                }
            }
        }

        return maxi;
    }

private:

    void help(vector<int>& row, deque<pair<long long, int>>& topVals) {
        priority_queue<pair<long long, int>> pq;


        for (int j = 0; j < row.size(); ++j) {
            pq.emplace(row[j], j);
        }


        for (int k = 0; k < 3 && !pq.empty(); ++k) {
            topVals.push_back(pq.top());
            pq.pop();
        }
    }
};
