#define ll long long
#define ff first
#define ss second

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
        vector<pair<ll,pair<int,int>>>v;
        int n=b.size(),m=b[0].size();
        for(int i=0;i<n;i++){
            vector<pair<ll,pair<int,int>>>d;
            for(int j=0;j<m;j++){
                d.push_back({b[i][j],{i,j}});
            }
            sort(d.begin(),d.end());
            reverse(d.begin(),d.end());
            v.push_back(d[1]);v.push_back(d[2]);
            v.push_back(d[0]);
        }
        sort(v.begin(),v.end());
        ll ans=INT64_MIN;
        int a=v.size();
        for(int i=0;i<a;i++){
            for(int j=i+1;j<a;j++){
                if(v[i].ss.ff==v[j].ss.ff || v[i].ss.ss==v[j].ss.ss){
                    continue;
                }
                for(int k=j+1;k<a;k++){
                    if(v[i].ss.ff!=v[j].ss.ff && v[i].ss.ff!=v[k].ss.ff && v[k].ss.ff!=v[j].ss.ff && v[i].ss.ss!=v[j].ss.ss && v[i].ss.ss!=v[k].ss.ss && v[k].ss.ss!=v[j].ss.ss ){
                        // cout<<v[i].ff<<" "<<v[j].ff<<" "<<v[k].ff<<endl;
                        ans=max(ans,v[i].ff+v[j].ff+v[k].ff);
                    }
                }
            }
        }
        return ans;
    }
};
