import java.util.*;

class Solution {
    public long maximumValueSum(int[][] board) {
        int rows = board.length;
        int cols = board[0].length;
        long maxSum = Long.MIN_VALUE;
        long[][] maxValues = new long[rows][3];
        int[][] columnIndices = new int[rows][3];

        for (int r = 0; r < rows; ++r) {
            List<Pair<Long, Integer>> valueIndexPairs = new ArrayList<>();
            for (int c = 0; c < cols; ++c) {
                valueIndexPairs.add(new Pair<>((long) board[r][c], c));
            }
            valueIndexPairs.sort((a, b) -> Long.compare(b.getKey(), a.getKey()));

            for (int i = 0; i < 3 && i < cols; ++i) {
                maxValues[r][i] = valueIndexPairs.get(i).getKey();
                columnIndices[r][i] = valueIndexPairs.get(i).getValue();
            }
        }

        for (int r1 = 0; r1 < rows; ++r1) {
            for (int r2 = r1 + 1; r2 < rows; ++r2) {
                for (int r3 = r2 + 1; r3 < rows; ++r3) {
                    for (int c1 = 0; c1 < 3; ++c1) {
                        for (int c2 = 0; c2 < 3; ++c2) {
                            if (columnIndices[r2][c2] == columnIndices[r1][c1]) continue;
                            for (int c3 = 0; c3 < 3; ++c3) {
                                if (columnIndices[r3][c3] == columnIndices[r1][c1] || columnIndices[r3][c3] == columnIndices[r2][c2]) continue;

                                long sum = maxValues[r1][c1] + maxValues[r2][c2] + maxValues[r3][c3];
                                maxSum = Math.max(maxSum, sum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }

    private static class Pair<K, V> {
        private final K key;
        private final V value;

        public Pair(K key, V value) {
            this.key = key;
            this.value = value;
        }

        public K getKey() {
            return key;
        }

        public V getValue() {
            return value;
        }
    }
}
