class Solution {

    public long maximumValueSum(int[][] board) {
        int rows = board.length;
        int cols = board[0].length;
        long maxSum = Long.MIN_VALUE;

        long[][] topValues = new long[rows][3];
        int[][] topColumns = new int[rows][3];

        for (int i = 0; i < rows; i++) {
            List<Pair> valueColumnPairs = new ArrayList<>();
            for (int j = 0; j < cols; j++) {
                valueColumnPairs.add(new Pair(board[i][j], j));
            }
            valueColumnPairs.sort((a, b) -> Long.compare(b.value, a.value)); // Sort in descending order

            for (int k = 0; k < 3 && k < cols; k++) {
                topValues[i][k] = valueColumnPairs.get(k).value;
                topColumns[i][k] = valueColumnPairs.get(k).column;
            }
        }

        // Try all combinations of 3 different rows
        for (int row1 = 0; row1 < rows; row1++) {
            for (int row2 = row1 + 1; row2 < rows; row2++) {
                for (int row3 = row2 + 1; row3 < rows; row3++) {
                    for (int col1 = 0; col1 < 3; col1++) {
                        for (int col2 = 0; col2 < 3; col2++) {
                            if (topColumns[row2][col2] == topColumns[row1][col1]) continue;
                            for (int col3 = 0; col3 < 3; col3++) {
                                if (topColumns[row3][col3] == topColumns[row1][col1] || topColumns[row3][col3] == topColumns[row2][col2]) continue;

                                long currentSum = topValues[row1][col1] + topValues[row2][col2] + topValues[row3][col3];
                                maxSum = Math.max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
    private static class Pair {
        long value;
        int column;

        Pair(long value, int column) {
            this.value = value;
            this.column = column;
        }
    }

}
