func maximumValueSum(board [][]int) int64 {
	var maxVal int64 = -3000000000
	lenRow := len(board)
	lenCol := len(board[0])

	maxArr1 := make([][3]int, 3)
	for i := range maxArr1 {
		maxArr1[i] = [3]int{-1000000001, -1, -1}
	}

	for i := 0; i < lenRow; i++ {
		maxRow := -1000000000
		maxJ := -1
		for j := 0; j < lenCol; j++ {
			if maxRow < board[i][j] {
				maxRow = board[i][j]
				maxJ = j
			}
		}
		if maxArr1[0][0] < maxRow {
			maxArr1[0], maxArr1[1], maxArr1[2] = [3]int{maxRow, i, maxJ}, maxArr1[0], maxArr1[1]
		} else if maxArr1[1][0] < maxRow {
			maxArr1[1], maxArr1[2] = [3]int{maxRow, i, maxJ}, maxArr1[1]
		} else if maxArr1[2][0] < maxRow {
			maxArr1[2] = [3]int{maxRow, i, maxJ}
		}
	}

	maxArr2 := make([][6]int, 9)
	for i := range maxArr2 {
		maxArr2[i] = [6]int{-1000000001, -1, -1, -1000000001, -1, -1}
	}
	for t := 0; t < 3; t++ {
		for i := 0; i < lenRow; i++ {
			if maxArr1[t][1] != i {
				maxRow := -1000000000
				maxJ := -1
				for j := 0; j < lenCol; j++ {
					if j != maxArr1[t][2] {
						if maxRow < board[i][j] {
							maxRow = board[i][j]
							maxJ = j
						}
					}
				}
				if maxArr2[0][3] < maxRow {
					maxArr2[t], maxArr2[t+3], maxArr2[t+6] = [6]int{maxArr1[t][0], maxArr1[t][1], maxArr1[t][2], maxRow, i, maxJ}, maxArr2[t], maxArr2[t+3]
				} else if maxArr2[t+3][3] < maxRow {
					maxArr2[t+3], maxArr2[t+6] = [6]int{maxArr1[t][0], maxArr1[t][1], maxArr1[t][2], maxRow, i, maxJ}, maxArr2[t]
				} else if maxArr2[t+6][3] < maxRow {
					maxArr2[t+6] = [6]int{maxArr1[t][0], maxArr1[t][1], maxArr1[t][2], maxRow, i, maxJ}
				}
			}
		}
	}

	for i := 0; i < 9; i++ {
		for x := 0; x < lenRow; x++ {
			if x != maxArr2[i][1] && x != maxArr2[i][4] {
				for z := 0; z < lenCol; z++ {
					if z != maxArr2[i][2] && z != maxArr2[i][5] {
						maxVal = max(maxVal, int64(maxArr2[i][0]+maxArr2[i][3]+board[x][z]))
					}
				}
			}
		}
	}
	return maxVal
}
