#include <vector>
#include <algorithm>
#include <climits>
using namespace std;

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& a) {
        int b = a.size();
        int c = a[0].size();
        long long d = LLONG_MIN;
        vector<vector<long long>> e(b, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> f(b, vector<int>(3, -1));
        for (int g = 0; g < b; ++g) {
            vector<pair<long long, int>> h;
            for (int i = 0; i < c; ++i) {
                h.emplace_back(a[g][i], i);
            }
            sort(h.rbegin(), h.rend());

            for (int j = 0; j < 3 && j < c; ++j) {
                e[g][j] = h[j].first;
                f[g][j] = h[j].second;
            }
        }

        for (int g1 = 0; g1 < b; ++g1) {
            for (int g2 = g1 + 1; g2 < b; ++g2) {
                for (int g3 = g2 + 1; g3 < b; ++g3) {
                    for (int h1 = 0; h1 < 3; ++h1) {
                        for (int h2 = 0; h2 < 3; ++h2) {
                            if (f[g2][h2] == f[g1][h1]) continue; 
                            for (int h3 = 0; h3 < 3; ++h3) {
                                if (f[g3][h3] == f[g1][h1] || f[g3][h3] == f[g2][h2]) continue;
                                long long i = e[g1][h1] + e[g2][h2] + e[g3][h3];
                                d = max(d, i);
                            }
                        }
                    }
                }
            }
        }

        return d;
    }
};
