class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {

        int m = board.size();
        int n = board[0].size();


        long long maxSum = LLONG_MIN;
        vector<vector<long long>> best(m, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> temp(m, vector<int>(3, -1));

        for (int i = 0; i < m; ++i) {
            vector<pair<long long, int>> colValues;
            for (int j = 0; j < n; ++j) {
                colValues.emplace_back(board[i][j], j);
            }
            sort(colValues.rbegin(), colValues.rend()); 

            for (int k = 0; k < 3 && k < n; ++k) {
                best[i][k] = colValues[k].first;
                temp[i][k] = colValues[k].second;
            }
        }

        // Try all combinations of all  3 different rows.
        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (temp[r2][j] == temp[r1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (temp[r3][k] == temp[r1][i] || temp[r3][k] == temp[r2][j]) continue;

                                long long currentSum = best[r1][i] + best[r2][j] + best[r3][k];
                                maxSum = max(maxSum, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
