#include <bits/stdc++.h>
using namespace std;


// #define int         long long
#define ll          long long
#define pb          push_back
#define pii         pair<int,int>
#define vi          vector<int>
#define vii         vector<pii>
#define mi          map<int,int>
#define mii         map<pii,int>
#define mp          make_pair
#define all(a)      (a).begin(),(a).end()
#define FF           first
#define SS           second
#define sz(x)       (int)x.size()
#define endl        '\n'
#define rep(i,a,b)  for(int i=a;i<b;i++)
int hell=1000000007;

class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans =  -3LL * hell;
        int n = board.size();
        int m = board[0].size();
        vector<pair<int,pii>> v;
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                v.pb({board[i][j],{i,j}});
            }
        }
        sort(all(v));
        int n1 = v.size();
        int operation = 1e7;
        reverse(all(v));
        int mn = 3*max(n,m) + 10;


        for(int i=0;i<n1;i++){
            auto x = v[i];
            int val = mn;
            for(int j=i+1;j<n1;j++){
                auto y = v[j];
                if(val == 0)break;
                val--;
                if(x.SS.FF == y.SS.FF || x.SS.SS == y.SS.SS)continue;
                int val1 = mn;
                for(int k=j+1;k<n1;k++){
                    auto z = v[k];
                    if(val1 ==0 )break;
                    val1--;
                    if(x.SS.FF == z.SS.FF || x.SS.SS == z.SS.SS || y.SS.FF == z.SS.FF || y.SS.SS == z.SS.SS)continue;
                    ans = max(ans , x.FF + 0LL + y.FF + 0LL + z.FF);
                    operation--;
                    if(operation == 0)return ans;
                }

            }
        } 
        return ans;
    }
};
