class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])

        # 预处理每一行的最大三个值及其对应的列
        top_three = []
        for i in range(m):
            row = [(board[i][j], j) for j in range(n)]
            row.sort(reverse=True, key=lambda x: x[0])
            top_three.append(row[:3])

        max_sum = float('-inf')

        # 枚举所有的行组合
        for r1 in range(m):
            for r2 in range(r1 + 1, m):
                for r3 in range(r2 + 1, m):
                    # 尝试不同的列组合，确保列不重复
                    for v1, c1 in top_three[r1]:
                        for v2, c2 in top_three[r2]:
                            if c1 == c2:
                                continue
                            for v3, c3 in top_three[r3]:
                                if c3 == c1 or c3 == c2:
                                    continue
                                current_sum = v1 + v2 + v3
                                max_sum = max(max_sum, current_sum)

        return max_sum
