class Solution {
public:
    long long max1 = -3 * 10e9;
    int n, m;
    int mm;
    vector<int> l = vector<int>(105, 0);
    vector<int> r = vector<int>(105, 0);
    void shousuo(int x, int y, int n1,long long sum, vector<vector<int>>& board) {
        if (n1 == 3) {
            if(max1<sum)
                max1 = sum ;
        }
        if(sum+(3-n1)*mm<max1){
            return;
        }
        for (int i = 0; i < n; i++) {
            if (l[i] == 1) {
                continue;
            }
            for (int j = 0; j < m; j++) {
                if (r[j] == 1) {
                    continue;
                }
                l[i] = 1;
                r[j] = 1;
                shousuo(i, j, n1 + 1, board[i][j] + sum, board);
                l[i] = 0;
                r[j] = 0;
            }
        }
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        n = board.size();
        m = board[0].size();
        mm=board[0][0];
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++){
                mm=mm>board[i][j]?mm:board[i][j];
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                l[i] = 1;
                r[j] = 1;
                shousuo(i, j, 1, board[i][j], board);
                l[i] = 0;
                r[j] = 0;
            }
        }
        return max1;
    }
};
