#include <ctime>
class Solution {
public:
    clock_t startTime;
    double getCurrentTime() {
        return (double)(clock() - startTime) / CLOCKS_PER_SEC;
    }
    long long maximumValueSum(vector<vector<int>>& board) {
        startTime = clock();
        vector<vector<long long>>vv;
        long long n = board.size();
        long long m = board.back().size();
        for(long long i = 0;i<n;i++){
            for(long long j = 0;j<m;j++){
                vv.push_back({board[i][j],i,j});
            }
        }
        sort(vv.rbegin(),vv.rend());
        vector<vector<long long>>v;
        int ct = 2*(n+m);
        for(int i = 0;i<min(ct,(int)vv.size());i++){
            v.push_back(vv[i]);
        }
        auto can = [&](long long i,long long j,vector<long long>&ii,vector<long long>&jj){
            for(auto it : ii)if(i==it)return false;
            for(auto it : jj)if(it==j)return false;
            return true;
        };
        map<pair<long long,pair<vector<long long>,vector<long long>>>,long long>mp;
        auto rec = [&](long long ind,vector<long long>&ii,vector<long long>&jj,auto&&rec)->long long{
            double t =  getCurrentTime();
            if(t>0.02)return -1e15;
            if(ind==v.size()){
                if(ii.size()!=3)return -1e15;
                return 0;
            }
            if(ii.size()==3)return 0;
            // if(mp.find({ind,{ii,jj}})!=mp.end())return mp[{ind,{ii,jj}}];
            long long ans = -1e15;
            if(can(v[ind][1],v[ind][2],ii,jj)){
                ii.push_back(v[ind][1]);
                jj.push_back(v[ind][2]);
                ans = v[ind][0]+rec(ind+1,ii,jj,rec);
                ii.pop_back();
                jj.pop_back();
            }
            ans = max(ans,rec(ind+1,ii,jj,rec));
            // mp[{ind,{ii,jj}}] = ans;
            return ans;
        };
        vector<long long>ii,jj;
        return rec(0ll,ii,jj,rec);
    }
};
