var maximumValueSum = function(matrix) {
    let rowCount = matrix.length;
    let colCount = matrix[0].length;
    let highestValue = -Infinity;

    let rowTopValues = Array.from({ length: rowCount }, () => Array(3).fill(-Infinity));
    let rowTopIndices = Array.from({ length: rowCount }, () => Array(3).fill(-1));

    for (let r = 0; r < rowCount; r++) {
        let valueList = [];
        for (let c = 0; c < colCount; c++) {
            valueList.push([matrix[r][c], c]);
        }
        valueList.sort((a, b) => b[0] - a[0]);

        for (let t = 0; t < 3 && t < colCount; t++) {
            rowTopValues[r][t] = valueList[t][0];
            rowTopIndices[r][t] = valueList[t][1];
        }
    }

    for (let firstRow = 0; firstRow < rowCount; firstRow++) {
        for (let secondRow = firstRow + 1; secondRow < rowCount; secondRow++) {
            for (let thirdRow = secondRow + 1; thirdRow < rowCount; thirdRow++) {
                for (let firstIdx = 0; firstIdx < 3; firstIdx++) {
                    for (let secondIdx = 0; secondIdx < 3; secondIdx++) {
                        if (rowTopIndices[secondRow][secondIdx] === rowTopIndices[firstRow][firstIdx]) continue;
                        for (let thirdIdx = 0; thirdIdx < 3; thirdIdx++) {
                            if (rowTopIndices[thirdRow][thirdIdx] === rowTopIndices[firstRow][firstIdx] || rowTopIndices[thirdRow][thirdIdx] === rowTopIndices[secondRow][secondIdx]) continue;

                            let sum = rowTopValues[firstRow][firstIdx] + rowTopValues[secondRow][secondIdx] + rowTopValues[thirdRow][thirdIdx];
                            highestValue = Math.max(highestValue, sum);
                        }
                    }
                }
            }
        }
    }

    return highestValue;
};
