class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int n=board.size();
        int m=board[0].size();
        int pre[n][m];
        int suf[n][m];
        for(int i=0;i<n;i++){
            pre[i][0]=board[i][0];
            for(int j=1;j<m;j++){
                pre[i][j]=max(pre[i][j-1],board[i][j]);
            }
            suf[i][m-1]=board[i][m-1];
            for(int j=m-2;j>=0;j--){
                suf[i][j]=max(suf[i][j+1],board[i][j]);
            }
        }
        long long res=-1e18;
        for(int i=0;i<n;i++){
            for(int j=i+1;j<n;j++){
                for(int k=j+1;k<n;k++){
                    for(int u=1;u<m-1;u++){
                        res=max(res,(long long)board[i][u]+pre[j][u-1]+suf[k][u+1]);
                        res=max(res, (long long) board[i][u]+pre[k][u-1]+suf[j][u+1]);
                        res=max(res,(long long) board[j][u]+pre[i][u-1]+suf[k][u+1]);
                        res=max(res, (long long) board[j][u]+pre[k][u-1]+suf[i][u+1]);
                        res=max(res,(long long) board[k][u]+pre[j][u-1]+suf[i][u+1]);
                        res=max(res,(long long) board[k][u]+pre[i][u-1]+suf[j][u+1]);
                    }
                }
            }
        }
        return res;
    }
};
