class Solution {
public:
    vector<vector<pair<int, int>>> v;
    long long check(vector<pair<int, int>> &aa, vector<pair<int, int>> &bb, vector<pair<int, int>> &cc) {
        long long ans = 0;
        ans = aa[0].first;
        int fb1 = aa[0].second;
        int fb2 = -1;
        if(bb[0].second == fb1) {
            ans += bb[1].first;
            fb2 = bb[1].second;
        } else {
            ans += bb[0].first;
            fb2 = bb[0].second;
        }
        for(int i = 0; i <= 2; i++) {
            if(cc[i].second == fb1 || cc[i].second == fb2) continue;
            ans += cc[i].first;
            break;
        }
        return ans;
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        long long ans = -1e13;
        for(auto & it : board) {
            vector<pair<int, int>>tmp;
            for(int i = 0; i < it.size(); i++) {
                tmp.emplace_back(it[i], i);
            }
            sort(tmp.begin(), tmp.end(), greater<pair<int, int>>());
            v.push_back(tmp);
        }

        int m = board.size(), n = board[0].size();
        for(int a = 0; a < m; a++) {
            for(int b = a + 1; b < m; b++) {
                for(int c = b + 1; c < m; c++) {

                    ans = max(ans, check(v[a], v[b], v[c]));
                    ans = max(ans, check(v[a], v[c], v[b]));
                    ans = max(ans, check(v[b], v[c], v[a]));
                    ans = max(ans, check(v[b], v[a], v[c]));
                    ans = max(ans, check(v[c], v[b], v[a]));
                    ans = max(ans, check(v[c], v[a], v[b]));
                }
            }
        }
        return ans;
    }
};
