class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& b) {
          int m = b.size();
        int n = b[0].size();
        long long maxSum = LLONG_MIN;
        vector<vector<long long>> v(m, vector<long long>(3, LLONG_MIN));
        vector<vector<int>> c(m, vector<int>(3, -1));

        for (int i = 0; i < m; ++i) {
            vector<pair<long long, int>> p;
            for (int j = 0; j < n; ++j) {
                p.emplace_back(b[i][j], j);
            }
            sort(p.rbegin(), p.rend());

            for (int k = 0; k < 3 && k < n; ++k) {
                v[i][k] = p[k].first;
                c[i][k] = p[k].second;
            }
        }

        // Try all combinations of 3 different rows
        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (c[r2][j] == c[r1][i]) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (c[r3][k] == c[r1][i] || c[r3][k] == c[r2][j]) continue;

                                long long s = v[r1][i] + v[r2][j] + v[r3][k];
                                maxSum = max(maxSum, s);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
