class Solution {
public:
    long long maximumValueSum(std::vector<std::vector<int>>& board) {
        int m = board.size();
        int n = board[0].size();
        std::vector<std::vector<std::pair<int, int>>> maxVals(m, std::vector<std::pair<int, int>>(3, {-INT_MAX, -1}));
        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                if (board[i][j] > maxVals[i][0].first) {
                    maxVals[i][2] = maxVals[i][1];
                    maxVals[i][1] = maxVals[i][0];
                    maxVals[i][0] = {board[i][j], j};
                } else if (board[i][j] > maxVals[i][1].first) {
                    maxVals[i][2] = maxVals[i][1];
                    maxVals[i][1] = {board[i][j], j};
                } else if (board[i][j] > maxVals[i][2].first) {
                    maxVals[i][2] = {board[i][j], j};
                }
            }
        }

        long long maxSum = LLONG_MIN;
        for (int r1 = 0; r1 < m; ++r1) {
            for (int r2 = r1 + 1; r2 < m; ++r2) {
                for (int r3 = r2 + 1; r3 < m; ++r3) {
                    for (int i = 0; i < 3; ++i) {
                        for (int j = 0; j < 3; ++j) {
                            if (maxVals[r2][j].second == maxVals[r1][i].second) continue;
                            for (int k = 0; k < 3; ++k) {
                                if (maxVals[r3][k].second == maxVals[r1][i].second || maxVals[r3][k].second == maxVals[r2][j].second) continue;

                                long long sum = static_cast<long long>(maxVals[r1][i].first) + maxVals[r2][j].first + maxVals[r3][k].first;
                                maxSum = std::max(maxSum, sum);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
