using ll = long long;
class Solution {
public:
    long long maximumValueSum(vector<vector<int>> &board) {
        ll m = board.size();
        ll n = board[0].size();
        ll ans = INT64_MIN;

        vector<vector<pair<ll, ll>>> top3(m, vector<pair<ll, ll>>(3, {-1, -1}));

        for (ll i = 0; i < m; i++) {
            vector<pair<ll, ll>> tmp;
            for (ll j = 0; j < n; j++) {
                tmp.push_back({board[i][j], j});
            }

            sort(tmp.rbegin(), tmp.rend());

            for (ll k = 0; k < 3 && k < n; k++) {
                top3[i][k] = tmp[k];
            }
        }

        for (ll i = 0; i < m; i++) {
            for (ll j = i + 1; j < m; j++) {
                for (ll k = j + 1; k < m; k++) {
                    for (const auto &[val1, col1] : top3[i]) {
                        for (const auto &[val2, col2] : top3[j]) {
                            if (col1 == col2)
                                continue;
                            for (const auto &[val3, col3] : top3[k]) {
                                if (col1 == col3 || col2 == col3)
                                    continue;
                                ll currentSum = val1 + val2 + val3;
                                ans = max(ans, currentSum);
                            }
                        }
                    }
                }
            }
        }

        return ans;
    }
};
