class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        m, n = len(board), len(board[0])


        top_3_per_row = []
        for i in range(m):

            top_3 = sorted(((board[i][j], j) for j in range(n)), reverse=True)[:3]
            top_3_per_row.append(top_3)

        max_sum = float('-inf')


        for rows in combinations(range(m), 3):

            for first in top_3_per_row[rows[0]]:
                for second in top_3_per_row[rows[1]]:
                    for third in top_3_per_row[rows[2]]:

                        if len({first[1], second[1], third[1]}) == 3:
                            current_sum = first[0] + second[0] + third[0]
                            max_sum = max(max_sum, current_sum)
                            break


        return max_sum
