class Solution:
    def maximumValueSum(self, board: List[List[int]]) -> int:
        new = []

        for col in board:
            t = [None] * len(col)
            for i in range(len(col)):
                t[i] = (col[i], i)
            t.sort(reverse=True)
            new.append(t[:3])

        # n * 3
        ans = board[0][0] + board[1][1] + board[2][2]
        for i1 in range(len(board)-2):
            for j1 in range(3):
                # first is new[i1][j1]
                r1 = new[i1][j1][1]
                for i2 in range(i1 + 1, len(board)-1):
                    for j2 in range(3):
                        r2 = new[i2][j2][1]
                        if r2 == r1:
                            continue
                        for i3 in range(i2 + 1, len(board)):
                            for j3 in range(3):
                                r3 = new[i3][j3][1]
                                if r3 == r1 or r3 == r2:
                                    continue
                                ans = max(ans, new[i1][j1][0] + new[i2][j2][0] + new[i3][j3][0])
        return ans
