class Solution {
public:
    long long ans = LLONG_MIN;
    int nr, nc;
    int vis_x[301] = {0};
    int vis_y[301] = {0};
    long long mx = INT_MIN;

    void dfs(vector<vector<int>>& board, long long path, int start) {
        if (start == 3) {
            ans = max(ans, path);
            return;
        }

        for (int i = 0; i < nr; i++) {
            if (vis_x[i]) continue;
            for (int j = 0; j < nc; j++) {
                if (vis_y[j]) continue;

                long long x = path + board[i][j];


                if (x + (2 - start) * mx <= ans) continue;

                vis_x[i] = 1;
                vis_y[j] = 1;
                dfs(board, x, start + 1);
                vis_x[i] = 0;
                vis_y[j] = 0;
            }
        }
    }

    long long maximumValueSum(vector<vector<int>>& board) {
        nr = board.size();
        nc = board[0].size();

        for (const auto& row : board) {
            for (int val : row) {
                mx = max(mx, (long long)val);
            }
        }

        dfs(board, 0, 0);

        return ans;
    }
};
