class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        vector<vector<pair<long long,pair<int,int>>>>a;
        int nr=0;
        for(vector<int>b:board){
            int maxi1=-1e9,maxi2=-1e9,maxi3=-1e9;
            int y1=-1,y2=-1,y3=-1;
            for(int i=0;i<b.size();i++){
                if(b[i]>maxi1){
                    maxi3=maxi2;
                    maxi2=maxi1;
                    maxi1=b[i];
                    y3=y2;
                    y2=y1;
                    y1=i;
                }
                else if(b[i]>=maxi2){
                    maxi3=maxi2;
                    maxi2=b[i];
                    y3=y2;
                    y2=i;
                }
                else if(b[i]>=maxi3){
                    maxi3=b[i];
                    y3=i;
                }
            }
            vector<pair<long long,pair<int,int>>>c;
            c.push_back({maxi1,{nr,y1}});
            c.push_back({maxi2,{nr,y2}});
            c.push_back({maxi3,{nr,y3}});
            a.push_back(c);
            nr++;
        }
        long long maxi=-1e10;
        for(int i=0;i<a.size()-2;i++){
            for(int j=i+1;j<a.size()-1;j++){
                for(int k=j+1;k<a.size();k++){
                    for(int i1=0;i1<3;i1++){
                        for(int j1=0;j1<3;j1++){
                            for(int k1=0;k1<3;k1++){
                             //   cout<<a[i][i1].first<<" "<<a[j][j1].first<<" "<<a[k][k1].first<<'\n';
                                //cout<<a[i][i1].second.second<<" "<<a[j][j1].second.second<<" "<<a[k][k1].second.second<<"\n";
                                if(a[i][i1].second.second!=a[j][j1].second.second&&a[j][j1].second.second!=a[k][k1].second.second&&a[i][i1].second.second!=a[k][k1].second.second){
                                    long long p=a[i][i1].first*1LL+a[j][j1].first*1LL+a[k][k1].first*1LL;
                                    if(p>maxi) maxi=p;
                                }
                            }
                        }
                    }
                }
            }
        }
        return maxi;
    }
};
