class Solution {
public:
    long long maximumValueSum(vector<vector<int>>& board) {
        int m = board.size();    
        int n = board[0].size();

        vector<vector<pair<int, int>>> rowTop(m), colTop(n);

        for (int i = 0; i < m; ++i) {
            for (int j = 0; j < n; ++j) {
                rowTop[i].emplace_back(board[i][j], j);
            }
            sort(rowTop[i].rbegin(), rowTop[i].rend());
            if (rowTop[i].size() > 3) rowTop[i].resize(3);
        }

        for (int j = 0; j < n; ++j) {
            for (int i = 0; i < m; ++i) {
                colTop[j].emplace_back(board[i][j], i);
            }
            sort(colTop[j].rbegin(), colTop[j].rend());
            if (colTop[j].size() > 3) colTop[j].resize(3);
        }

        long long maxSum = LLONG_MIN;

        for (int r1 = 0; r1 < m; ++r1) {
            for (const auto& [val1, c1] : rowTop[r1]) {
                for (int r2 = 0; r2 < m; ++r2) {
                    if (r2 == r1) continue;
                    for (const auto& [val2, c2] : rowTop[r2]) {
                        if (c2 == c1) continue;
                        for (int r3 = 0; r3 < m; ++r3) {
                            if (r3 == r1 || r3 == r2) continue;
                            for (const auto& [val3, c3] : rowTop[r3]) {
                                if (c3 == c1 || c3 == c2) continue;
                                maxSum = max(maxSum, (long long)val1 + val2 + val3);
                            }
                        }
                    }
                }
            }
        }

        return maxSum;
    }
};
